@import url("https://st-repo.kakaa.win/theme/双色盒子/双色盒子.css");

@import url("https://fontsapi.zeoseven.com/285/main/result.css");

body *:not(.fa):not(.fas):not(.fab):not(.fa-solid):not(.fa-brands) {
  font-family: "Noto Serif CJK" !important;
  font-weight: 600 !important;
}

#chat {
-webkit-backdrop-filter: blur(10px) !important;
backdrop-filter: blur(10px) !important;
letter-spacing: 0.5px;
}

div[is_user="false"] .avatar img{
box-shadow: 0 0 20px 1px color-mix(in srgb, var(--theme-color-2) 40%, transparent)
}

div[is_user="true"] .avatar img{
box-shadow: 0 0 20px 1px color-mix(in srgb, var(--theme-color-1) 40%, transparent)
}
